import React, { Component } from 'react'
import './Form.css';

class Form extends Component {
  constructor() {
    super()

    this.state = {
      nome: '',
      idade: 0
    }
    this.handleChanges = this.handleChanges.bind(this)
  }

  handleChanges({ target }) {
    const { name } = target;
    const { value } = target.type === 'checkbox' ? target.checked : target.value;

    this.setState({
      [name]: value
    })
  }

  render() {
    return (
      <div>
        <h1>Fixação aula Forms!</h1>
        <form className="form">
          <label>
            Escolha alguma coisa legal:
            <select name='coisas'>
              <option>Coisa 1</option>
              <option>Coisa 2</option>
              <option>Coisa 3</option>
              <option>Coisa 4</option>            
            </select>
          </label>
          <label>
            Digite seu nome:
            <input name='nome' type='text' value={this.state.name} onChange={this.handleChanges}></input>
          </label>
          <label>
            Digite sua idade:
            <input name='idade' type='number'></input>
          </label>
          <label>
            Digite algo sobre vc:
            <textarea name='sobre'></textarea>
          </label>
        </form>
      </div>
    );
  }
}

export default Form;
