import React, { Component } from 'react'
import './Form.css';

class Form extends Component {
  constructor() {
    super()

    this.state = {
      name: ''
    }
  }

  handleName() {

  }

  render() {
    return (
      <div>
        <h1>Fixação aula Forms!</h1>
        <form className="form">
          <label>
            Escolha alguma coisa legal:
            <select name='coisas'>
              <option>Coisa 1</option>
              <option>Coisa 2</option>
              <option>Coisa 3</option>
              <option>Coisa 4</option>            
            </select>
          </label>
          <label>
            Digite seu nome:
            <input name='nome' type='text'></input>
          </label>
          <label>
            Digite sua idade:
            <input name='idade' type='number' value=''></input>
          </label>
          <label>
            Digite algo sobre vc:
            <textarea name='sobre'></textarea>
          </label>
        </form>
      </div>
    );
  }
}

export default Form;
